import { GET_PRODUCTS } from "../actionstypes/actionTypes";
//Get product
export const getProducts=()=>(dispatch)=>{
    axios.get("/api/product")
    .then((res)=>dispatch({type:GET_PRODUCTS,payload:res.data}))
    .catch((err)=>console.log(err))
}
