import { ADD_TO_CART } from "../actionstypes/actionTypes";
const initState={
    cart: {
        cartItems: localStorage.getItem('cartItems')
          ? JSON.parse(localStorage.getItem('cartItems'))
          : [],
}
}
const cartReducer=(state=initstate,action)=>{
    switch(action.type){
        case ADD_TO_CART:
        return{
            ...state,
            cart:action.payload
            };
            default:
                return state
        }
    }
export default cartReducer
