import { GET_PRODUCTS } from "../actionstypes/actionTypes";
import axios from "axios"
//Get product
export const getProducts=()=>(dispatch)=>{
    axios.get("/api/product")
    .then((res)=>dispatch({type:GET_PRODUCTS,payload:res.data}))
    .catch((err)=>console.log(err))
}
export const addProduct=(newProduct)=>(dispatch)=>{
    axios.get("/api/product/add",newProduct)
    .then((res)=>dispatch(getProducts()))
    .catch((err)=>console.log(err))
}
export const deleteProduct=(newProduct)=>(dispatch)=>{
    axios.get(`/api/product/${idProduct}`,newProduct)
    .then((res)=>dispatch(getProducts()))
    .catch((err)=>console.log(err))
}
